// recursiveBinarySearch.java// Assumes array is in sorted order.// Returns index of the location of the key in the array.// Retuns â€“1 if not found.public int recursiveBinarySearch(int[] list, int i, int j, int key) {    if (i > j)        return -1;    int mid = (i + j) / 2;    if (list[mid] > key)        return recursiveBinarySearch(list, i, mid-1, key);    else if (list[mid] < key)        return recursiveBinarySearch(list, mid+1, j, key);    else        return mid;}